import { ContentTypeId, ContentCodec, EncodedContent } from '@xmtp/content-type-primitives';

declare const ContentTypeText: ContentTypeId;
declare enum Encoding {
    utf8 = "UTF-8"
}
declare class TextCodec implements ContentCodec<string> {
    get contentType(): ContentTypeId;
    encode(content: string): EncodedContent;
    decode(content: EncodedContent): string;
    fallback(): undefined;
    shouldPush(): boolean;
}

export { ContentTypeText, Encoding, TextCodec };
